import React, { useEffect, useState } from "react";
import Edit from "../img/edit.png";
import Delete from "../img/delete.png";
import { Link, useLocation } from "react-router-dom";
import Menu from "../components/Menu";
import axios from "axios";
import moment from "moment";
import { useContext } from "react";
import { AuthContext } from "../context/authContext";

const Single = () => {
  const [post, setPost] = useState({});

  const location = useLocation();

  const postId = location.pathname.split("/")[2];  //////////////// 1:48:30

  const {currentUser} = useContext(AuthContext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/posts/${postId}`);
        setPost(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [postId]);
  return (
    <div className="single">
      <div className="content">
        <img
          src={post?.img}
          alt=""
        />
        <div className="user">
          <img
            src="https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
            alt=""
          />
          <div className="info">
            <span>{post.username}</span>
            <p>Posted {moment(post.date).fromNow()}</p>
          </div>
          {currentUser.username === post.username && <div className="edit">
            <Link to={`/write?edit=2`}>
              <img src={Edit} alt="" />
            </Link>
            <img src={Delete} alt="" />
          </div>}
        </div>
        <h1>
          {post.title}
        </h1>
        <p>
          Lorem ipsum, dolor sit amet consectetur adipisicing elit. Facere, modi
          dolorem, quidem harum soluta nemo cupiditate ab excepturi nesciunt
          praesentium dignissimos officia quas! Similique assumenda, modi fugit
          officia fugiat praesentium animi veritatis quia omnis perferendis
          quaerat libero voluptatibus error. Totam ullam consequuntur magnam rem
          facere! Commodi, velit? Cumque, fuga distinctio!
        </p>
        <p>
          Lorem ipsum, dolor sit amet consectetur adipisicing elit. Facere, modi
          dolorem, quidem harum soluta nemo cupiditate ab excepturi nesciunt
          praesentium dignissimos officia quas! Similique assumenda, modi fugit
          officia fugiat praesentium animi veritatis quia omnis perferendis
          quaerat libero voluptatibus error. Totam ullam consequuntur magnam rem
          facere! Commodi, velit? Cumque, fuga distinctio!
        </p>
      </div>
      <Menu />
    </div>
  );
};

export default Single;
